/** @format */
import React, { useState, useEffect } from "react";
import { Input } from "../components/ui/input";
import { Label } from "../components/ui/label";
import { Textarea } from "../components/ui/textarea";
import { Button } from "../components/ui/button";
import { toast } from "sonner";
import { useNavigate } from "react-router-dom";

// Simple icon components
const Icon = ({ children, className = "" }) => (
  <span className={`inline-flex items-center justify-center ${className}`}>
    {children}
  </span>
);

const UploadProject = () => {
 const [projectTitle, setProjectTitle] = useState("");
 const [projectDescription, setProjectDescription] = useState("");
 const [projectFile, setProjectFile] = useState(null);
 const [category, setCategory] = useState("");

 const [mentors, setMentors] = useState([]);
 const [selectedMentor, setSelectedMentor] = useState(null);

 const [students, setStudents] = useState([]);
 const [hasTeammates, setHasTeammates] = useState("no");
 const [teamCount, setTeamCount] = useState(0);
 const [selectedTeammates, setSelectedTeammates] = useState([]);

 const [loading, setLoading] = useState(false);
 const navigate = useNavigate();

 // ✅ Fetch mentors
 useEffect(() => {
  const fetchMentors = async () => {
   try {
    const res = await fetch("http://127.0.0.1:5000/api/mentors/my-university", {
     headers: { Authorization: `Bearer ${localStorage.getItem("token")}` },
    });
    const data = await res.json();
    if (res.ok) setMentors(data.mentors || []);
    else toast.error(data.msg || "Failed to load mentors");
   } catch {
    toast.error("Could not fetch mentors");
   }
  };
  fetchMentors();
 }, []);

 // ✅ Fetch students
 useEffect(() => {
  const fetchStudents = async () => {
   try {
    const res = await fetch(
     "http://127.0.0.1:5000/api/students/my-university",
     {
      headers: { Authorization: `Bearer ${localStorage.getItem("token")}` },
     }
    );
    const data = await res.json();
    if (res.ok) setStudents(data.students || []);
    else toast.error(data.msg || "Failed to load students");
   } catch {
    toast.error("Could not fetch students");
   }
  };
  fetchStudents();
 }, []);

 // ✅ Reset teammates when count changes
 useEffect(() => {
  if (teamCount > 0) {
   setSelectedTeammates(Array.from({ length: teamCount }, () => null));
  } else {
   setSelectedTeammates([]);
  }
 }, [teamCount]);

 // ✅ Select teammate
 const handleSelectTeammate = (index, studentId) => {
  const student = students.find((s) => s._id === studentId);
  if (!student) return;

  if (selectedTeammates.some((t) => t?._id === studentId)) {
   toast.error("This student is already selected.");
   return;
  }

  const updated = [...selectedTeammates];
  updated[index] = {
   _id: student._id,
   name: student.name,
   email: student.email,
  };
  setSelectedTeammates(updated);
 };

 // ✅ Submit form
 const handleSubmit = async (e) => {
  e.preventDefault();

  if (!projectFile) return toast.error("Please select a file!");
  if (!category) return toast.error("Please select a category!");
  if (!selectedMentor) return toast.error("Please select a mentor!");

  if (hasTeammates === "yes" && selectedTeammates.includes(null)) {
   return toast.error("Please select all teammates!");
  }

  setLoading(true);

  const user = JSON.parse(localStorage.getItem("user"));

  // ✅ Build final teammates list including uploader
  const finalTeammates = [
   {
    _id: user._id,
    name: user.name,
    email: user.email,
   },
   ...selectedTeammates.filter(Boolean),
  ];

  const formData = new FormData();
  formData.append("title", projectTitle);
  formData.append("description", projectDescription);
  formData.append("file", projectFile);
  formData.append("category", category);
  formData.append("uploadedBy", user._id);

  // ✅ Mentor object
  formData.append("mentor", JSON.stringify(selectedMentor));

  // ✅ Teammate details (including uploader)
  formData.append("hasTeammates", hasTeammates);
  formData.append("teamCount", teamCount);
  formData.append("teammates", JSON.stringify(finalTeammates));

  try {
   const res = await fetch("http://127.0.0.1:5000/api/projects/upload", {
    method: "POST",
    headers: { Authorization: `Bearer ${localStorage.getItem("token")}` },
    body: formData,
   });

   const data = await res.json();

   if (res.ok && data.project?._id) {
    toast.success("Project uploaded successfully!");

    // ✅ Reset form
    setProjectTitle("");
    setProjectDescription("");
    setProjectFile(null);
    setCategory("");
    setSelectedMentor(null);
    setHasTeammates("no");
    setTeamCount(0);
    setSelectedTeammates([]);

    setTimeout(() => navigate("/projects", { replace: true }), 1200);
   } else {
    toast.error(data.msg || "Failed to upload project");
   }
  } catch (error) {
   toast.error("Something went wrong!");
  } finally {
   setLoading(false);
  }
 };

 return (
  <div className="max-w-md mx-auto p-6 bg-white rounded shadow">
   <h2 className="text-2xl font-bold mb-4">Upload New Project</h2>

   <form onSubmit={handleSubmit} className="space-y-4">
    {/* Title */}
    <div className="space-y-2">
     <Label>Project Title *</Label>
     <Input
      value={projectTitle}
      onChange={(e) => setProjectTitle(e.target.value)}
      required
     />
    </div>

    {/* Description */}
    <div className="space-y-2">
     <Label>Description *</Label>
     <Textarea
      value={projectDescription}
      onChange={(e) => setProjectDescription(e.target.value)}
      required
     />
    </div>

    {/* Category */}
    <div className="space-y-2">
     <Label>Category *</Label>
     <select
      className="w-full border p-2 rounded"
      value={category}
      onChange={(e) => setCategory(e.target.value)}
      required>
      <option value="">Select Category</option>
      <option value="Artificial Intelligence">Artificial Intelligence</option>
      <option value="Blockchain">Blockchain</option>
      <option value="Cloud Computing">Cloud Computing</option>
      <option value="Web Development">Web Development</option>
      <option value="Mobile Development">Mobile Development</option>
      <option value="Data Science">Data Science</option>
      <option value="Cybersecurity">Cybersecurity</option>
      <option value="IoT & Embedded">IoT & Embedded</option>
     </select>
    </div>

    {/* Mentor */}
    <div className="space-y-2">
     <Label>Select Mentor *</Label>
     <select
      className="w-full border p-2 rounded"
      value={selectedMentor?._id || ""}
      onChange={(e) => {
       const mentor = mentors.find((m) => m._id === e.target.value);
       if (mentor) {
        setSelectedMentor({
         _id: mentor._id,
         name: mentor.name,
         email: mentor.email,
        });
       }
      }}
      required>
      <option value="">Select Mentor</option>
      {mentors.map((m) => (
       <option key={m._id} value={m._id}>
        {m.name} ({m.email})
       </option>
      ))}
     </select>
    </div>

    {/* Teammate Question */}
    <div className="space-y-2">
     <Label>Do you have teammates?</Label>
     <select
      className="w-full border p-2 rounded"
      value={hasTeammates}
      onChange={(e) => {
       setHasTeammates(e.target.value);
       if (e.target.value === "no") {
        setTeamCount(0);
        setSelectedTeammates([]);
       }
      }}>
      <option value="no">No</option>
      <option value="yes">Yes</option>
     </select>
    </div>

    {/* Team Count */}
    {hasTeammates === "yes" && (
     <div className="space-y-2">
      <Label>How many teammates?</Label>
      <select
       className="w-full border p-2 rounded"
       value={teamCount}
       onChange={(e) => setTeamCount(Number(e.target.value))}>
       <option value="0">Select Count</option>
       {[1, 2, 3, 4, 5].map((num) => (
        <option key={num} value={num}>
         {num}
        </option>
       ))}
      </select>
     </div>
    )}

    {/* Dynamic teammate dropdowns */}
    {teamCount > 0 &&
     selectedTeammates.map((value, index) => (
      <div key={index} className="border p-3 rounded bg-gray-50 mt-3">
       <Label>Teammate {index + 1}</Label>
       <select
        className="w-full border p-2 rounded"
        value={value?._id || ""}
        onChange={(e) => handleSelectTeammate(index, e.target.value)}
        required>
        <option value="">Select a student</option>
        {students.map((s) => (
         <option key={s._id} value={s._id}>
          {s.name} ({s.email})
         </option>
        ))}
       </select>
      </div>
     ))}

    {/* File upload */}
    <div className="space-y-2">
     <Label>Upload File *</Label>
     <Input
      type="file"
      accept=".pdf,.pptx,.docx,.txt"
      onChange={(e) => setProjectFile(e.target.files[0])}
      required
     />
    </div>

    <Button disabled={loading} className="w-full">
     {loading ? "Uploading..." : "Submit Project"}
    </Button>
   </form>
  </div>
 );
};

export default UploadProject;
