/** @format */
import React, { useState, useEffect } from "react";
import { Input } from "../components/ui/input";
import { Label } from "../components/ui/label";
import { Textarea } from "../components/ui/textarea";
import { Button } from "../components/ui/button";
import { toast } from "sonner";
import { useNavigate } from "react-router-dom";

const UploadProject = () => {
 const [projectTitle, setProjectTitle] = useState("");
 const [projectDescription, setProjectDescription] = useState("");
 const [projectFile, setProjectFile] = useState(null);
 const [category, setCategory] = useState("");
 const [mentors, setMentors] = useState([]);
 const [selectedMentor, setSelectedMentor] = useState("");
 const [loading, setLoading] = useState(false);

 const navigate = useNavigate();

 // ✅ Fetch mentors of student's university
 useEffect(() => {
  const fetchMentors = async () => {
   try {
    const res = await fetch("http://127.0.0.1:5000/api/mentors/my-university", {
     headers: { Authorization: `Bearer ${localStorage.getItem("token")}` },
    });

    const data = await res.json();

    if (res.ok) {
     setMentors(data.mentors || []);
    } else {
     toast.error(data.msg || "Failed to load mentors");
    }
   } catch (error) {
    toast.error("Could not fetch mentors");
   }
  };

  fetchMentors();
 }, []);

 // ✅ Submit handler
 const handleSubmit = async (e) => {
  e.preventDefault();

  if (!projectFile) return toast.error("Please select a file!");
  if (!category) return toast.error("Please select a category!");
  if (!selectedMentor) return toast.error("Please select a mentor!");

  setLoading(true);

  const user = JSON.parse(localStorage.getItem("user"));

  const formData = new FormData();
  formData.append("title", projectTitle);
  formData.append("description", projectDescription);
  formData.append("file", projectFile);
  formData.append("category", category);
  formData.append("mentorId", selectedMentor);
  formData.append("uploadedBy", user._id);

  try {
   const res = await fetch("http://127.0.0.1:5000/api/projects/upload", {
    method: "POST",
    headers: {
     Authorization: `Bearer ${localStorage.getItem("token")}`,
    },
    body: formData,
   });

   const data = await res.json();

   if (res.ok && data.project?._id) {
    toast.success("Project uploaded successfully!");

    // Reset state
    setProjectTitle("");
    setProjectDescription("");
    setProjectFile(null);
    setCategory("");
    setSelectedMentor("");

    setTimeout(() => navigate("/projects", { replace: true }), 1200);
   } else {
    toast.error(data.msg || "Failed to upload project");
   }
  } catch (error) {
   console.error(error);
   toast.error("Something went wrong!");
  } finally {
   setLoading(false);
  }
 };

 return (
  <div className="max-w-md mx-auto p-6 bg-white rounded shadow">
   <h2 className="text-2xl font-bold mb-4">Upload New Project</h2>

   <form onSubmit={handleSubmit} className="space-y-4">
    <div className="space-y-2">
     <Label htmlFor="title">Project Title *</Label>
     <Input
      id="title"
      placeholder="Enter project title"
      value={projectTitle}
      onChange={(e) => setProjectTitle(e.target.value)}
      required
     />
    </div>

    <div className="space-y-2">
     <Label htmlFor="description">Description *</Label>
     <Textarea
      id="description"
      placeholder="Enter project description"
      value={projectDescription}
      onChange={(e) => setProjectDescription(e.target.value)}
      required
     />
    </div>

    <div className="space-y-2">
     <Label>Category *</Label>
     <select
      className="w-full border p-2 rounded"
      value={category}
      onChange={(e) => setCategory(e.target.value)}
      required>
      <option value="">Select Category</option>
      <option value="Artificial Intelligence">Artificial Intelligence</option>
      <option value="Blockchain">Blockchain</option>
      <option value="Cloud Computing">Cloud Computing</option>
      <option value="Web Development">Web Development</option>
      <option value="Mobile Development">Mobile Development</option>
      <option value="Data Science">Data Science</option>
      <option value="Cybersecurity">Cybersecurity</option>
      <option value="IoT & Embedded">IoT & Embedded</option>
     </select>
    </div>

    {/* ✅ Mentor Dropdown */}
    <div className="space-y-2">
     <Label>Select Mentor *</Label>
     <select
      className="w-full border p-2 rounded"
      value={selectedMentor}
      onChange={(e) => setSelectedMentor(e.target.value)}
      required>
      <option value="">Select Mentor</option>
      {mentors.length === 0 && (
       <option disabled>No mentors found for your university</option>
      )}
      {mentors.map((m) => (
       <option key={m._id} value={m._id}>
        {m.name} ({m.email})
       </option>
      ))}
     </select>
    </div>

    <div className="space-y-2">
     <Label>Upload File *</Label>
     <Input
      type="file"
      accept=".pdf,.pptx,.docx,.txt"
      onChange={(e) => setProjectFile(e.target.files[0])}
      required
     />
    </div>

    <Button disabled={loading} className="w-full">
     {loading ? "Uploading..." : "Submit Project"}
    </Button>
   </form>
  </div>
 );
};

export default UploadProject;
