/** @format */

import { useParams, Link } from "react-router-dom";
import { useState, useEffect } from "react";
import Navbar from "../components/Navbar";
import Footer from "../components/Footer";
import { Button } from "../components/ui/button";
import { Card, CardContent, CardHeader } from "../components/ui/card";
import { Badge } from "../components/ui/badge";
import { Separator } from "../components/ui/separator";
import { Avatar, AvatarFallback } from "../components/ui/avatar";
import {
 Calendar,
 Users,
 Eye,
 Download,
 Share2,
 ExternalLink,
 ArrowLeft,
} from "lucide-react";
import { toast } from "sonner";

const ProjectDetail = () => {
 const { id } = useParams();
 const [project, setProject] = useState(null);
 const [loading, setLoading] = useState(true);

 const token = localStorage.getItem("token");

 useEffect(() => {
  const fetchProject = async () => {
   try {
    const res = await fetch(`http://127.0.0.1:5000/api/projects/${id}`, {
     headers: { Authorization: `Bearer ${token}` },
    });
    if (!res.ok) throw new Error("Project not found");
    const data = await res.json();
    setProject(data.project);
   } catch (err) {
    console.error(err);
    toast.error("Failed to load project details");
   } finally {
    setLoading(false);
   }
  };

  fetchProject();
 }, [id, token]);

 if (loading) return <p className="text-center mt-10">Loading...</p>;
 if (!project) return <p className="text-center mt-10">Project not found</p>;

 const handleShare = () => {
  navigator.clipboard.writeText(window.location.href);
  toast.success("Link copied to clipboard!");
 };

 const handleDownload = () => {
  toast.success("Download started!");
 };

 return (
  <div className="min-h-screen flex flex-col">
   <main className="flex-1">
    {/* Breadcrumb */}
    <section className="py-4 border-b bg-muted/30">
     <div className="container">
      <Button variant="ghost" asChild className="mb-4">
       <Link to="/explore-projects">
        <ArrowLeft className="mr-2 h-4 w-4" /> Back to Projects
       </Link>
      </Button>
     </div>
    </section>

    {/* Project Header */}
    <section className="py-12 gradient-hero">
     <div className="container">
      <div className="max-w-4xl">
       <Badge className="mb-4 text-4xl">{project.category}</Badge>
       <h1 className="text-4xl md:text-5xl font-bold mb-4">{project.title}</h1>
       <p className="text-xl text-muted-foreground mb-6">
        {project.description}
       </p>

       <div className="flex flex-wrap items-center gap-6 text-sm text-muted-foreground mb-6">
        <div className="flex items-center gap-2">
         <Calendar className="h-4 w-4" />
         <span>{project.publishDate}</span>
        </div>
        <div className="flex items-center gap-2">
         <Users className="h-4 w-4" />
         <span>{project.members?.length || 0} members</span>
        </div>
        <div className="flex items-center gap-2">
         <Eye className="h-4 w-4" />
         <span>{project.views}</span>
        </div>
        <div className="flex items-center gap-2">
         <Download className="h-4 w-4" />
         <span>{project.downloads}</span>
        </div>
       </div>

       <div className="flex gap-3">
        <Button variant="hero" onClick={handleDownload}>
         <Download className="mr-2 h-4 w-4" />
         Download Project
        </Button>
        <Button variant="outline" onClick={handleShare}>
         <Share2 className="mr-2 h-4 w-4" />
         Share
        </Button>
        <Button variant="outline" asChild>
         <a
          href={project.sourceUrl || "#"}
          target="_blank"
          rel="noopener noreferrer">
          <ExternalLink className="mr-2 h-4 w-4" />
          View Source
         </a>
        </Button>
       </div>
      </div>
     </div>
    </section>

    {/* Project Content */}
    <section className="py-12">
     <div className="container">
      <div className="grid lg:grid-cols-3 gap-8">
       {/* Main Content */}
       <div className="lg:col-span-2 space-y-8">
        <Card className="gradient-card shadow-soft">
         <CardHeader>
          <h2 className="text-2xl font-bold">About This Project</h2>
         </CardHeader>
         <CardContent className="prose prose-slate max-w-none">
          <p className="whitespace-pre-line text-muted-foreground">
           {project.fullDescription || project.description}
          </p>
         </CardContent>
        </Card>

        <Card className="gradient-card shadow-soft">
         <CardHeader>
          <h2 className="text-2xl font-bold">Objectives</h2>
         </CardHeader>
         <CardContent>
          <ul className="space-y-2">
           {project.objectives?.map((obj, idx) => (
            <li key={idx} className="flex items-start gap-2">
             <span className="text-primary mt-1">•</span>
             <span className="text-muted-foreground">{obj}</span>
            </li>
           ))}
          </ul>
         </CardContent>
        </Card>

        <Card className="gradient-card shadow-soft">
         <CardHeader>
          <h2 className="text-2xl font-bold">Results & Impact</h2>
         </CardHeader>
         <CardContent>
          <ul className="space-y-2">
           {project.results?.map((res, idx) => (
            <li key={idx} className="flex items-start gap-2">
             <span className="text-primary mt-1">✓</span>
             <span className="text-muted-foreground">{res}</span>
            </li>
           ))}
          </ul>
         </CardContent>
        </Card>
       </div>

       {/* Sidebar */}
       <div className="space-y-6">
        <Card className="gradient-card shadow-soft">
         <CardHeader>
          <h3 className="font-semibold">Project Team</h3>
         </CardHeader>
         <CardContent className="space-y-4">
          {project.members?.map((member, idx) => (
           <div key={idx} className="flex items-center gap-3">
            <Avatar>
             <AvatarFallback className="gradient-primary text-primary-foreground">
              {member.initials}
             </AvatarFallback>
            </Avatar>
            <div>
             <p className="font-medium">{member.name}</p>
             <p className="text-sm text-muted-foreground">{member.role}</p>
            </div>
           </div>
          ))}
         </CardContent>
        </Card>

        <Card className="gradient-card shadow-soft">
         <CardHeader>
          <h3 className="font-semibold">Institution</h3>
         </CardHeader>
         <CardContent>
          <p className="text-sm text-muted-foreground">{project.university}</p>
         </CardContent>
        </Card>

        <Card className="gradient-card shadow-soft">
         <CardHeader>
          <h3 className="font-semibold">Technologies Used</h3>
         </CardHeader>
         <CardContent>
          <div className="flex flex-wrap gap-2">
           {project.technologies?.map((tech, idx) => (
            <Badge key={idx} variant="secondary">
             {tech}
            </Badge>
           ))}
          </div>
         </CardContent>
        </Card>

        <Card className="gradient-card shadow-soft">
         <CardHeader>
          <h3 className="font-semibold">Project Stats</h3>
         </CardHeader>
         <CardContent className="space-y-3">
          <div className="flex justify-between text-sm">
           <span className="text-muted-foreground">Views</span>
           <span className="font-medium">{project.views}</span>
          </div>
          <Separator />
          <div className="flex justify-between text-sm">
           <span className="text-muted-foreground">Downloads</span>
           <span className="font-medium">{project.downloads}</span>
          </div>
          <Separator />
          <div className="flex justify-between text-sm">
           <span className="text-muted-foreground">Published</span>
           <span className="font-medium">{project.publishDate}</span>
          </div>
         </CardContent>
        </Card>
       </div>
      </div>
     </div>
    </section>
   </main>

   <Footer />
  </div>
 );
};

export default ProjectDetail;
