import React, { useEffect, useState } from "react";
import { useNavigate, Link } from "react-router-dom";

import { Button } from "../components/ui/button";
import {
 Card,
 CardContent,
 CardHeader,
 CardTitle,
} from "../components/ui/card";
import { Badge } from "../components/ui/badge";
import {
 Table,
 TableBody,
 TableCell,
 TableHead,
 TableHeader,
 TableRow,
} from "../components/ui/table";

import {
 FolderOpen,
 Eye,
 Download,
 TrendingUp,
 Edit,
 Trash2,
 Plus,
} from "lucide-react";

import Footer from "../components/Footer";
import EditProjectPopup from "./EditProjectPopup";

const Dashboard = () => {
 const navigate = useNavigate();
 const [myProjects, setMyProjects] = useState([]);
 const [loading, setLoading] = useState(true);

 const [editOpen, setEditOpen] = useState(false);
 const [selectedProject, setSelectedProject] = useState(null);

 useEffect(() => {
  fetchMyProjects();
 }, []);

 // ✅ FIXED fetch logic
 async function fetchMyProjects() {
  try {
   const token = localStorage.getItem("token");

   const res = await fetch("http://127.0.0.1:5000/api/projects/my", {
    headers: {
     Authorization: `Bearer ${token}`,
    },
   });

   const data = await res.json();

   // ✅ Backend now returns an ARRAY directly, not { projects: [] }
   if (Array.isArray(data)) {
    setMyProjects(data);
   } else if (Array.isArray(data.projects)) {
    // fallback
    setMyProjects(data.projects);
   } else {
    setMyProjects([]);
   }
  } catch (err) {
   console.error("Error fetching user projects:", err);
   setMyProjects([]);
  }

  setLoading(false);
 }

 function openEditPopup(project) {
  setSelectedProject(project);
  setEditOpen(true);
 }

 async function handleEditSave(formData, id) {
  try {
   const token = localStorage.getItem("token");

   const res = await fetch(`http://127.0.0.1:5000/api/projects/${id}`, {
    method: "PUT",
    headers: {
     Authorization: `Bearer ${token}`,
    },
    body: formData,
   });

   const data = await res.json();

   if (res.ok) {
    alert("Project updated successfully!");
    setEditOpen(false);
    fetchMyProjects();
   } else {
    alert(data.msg || "Failed to update project");
   }
  } catch (err) {
   console.error(err);
   alert("Error updating project");
  }
 }

 async function deleteProject(id) {
  if (!window.confirm("Are you sure you want to delete this project?")) return;

  try {
   const token = localStorage.getItem("token");

   const res = await fetch(`http://127.0.0.1:5000/api/projects/${id}`, {
    method: "DELETE",
    headers: {
     Authorization: `Bearer ${token}`,
    },
   });

   const data = await res.json();

   if (res.ok) {
    alert("Project deleted successfully!");
    fetchMyProjects();
   } else {
    alert(data.msg || "Failed to delete project");
   }
  } catch (err) {
   console.error(err);
   alert("Error while deleting project");
  }
 }

 // ✅ safe stats block
 const stats = [
  {
   icon: FolderOpen,
   label: "Total Projects",
   value: (myProjects?.length || 0).toString(),
   color: "text-blue-500",
  },
  {
   icon: Eye,
   label: "Total Views",
   value: "—",
   color: "text-green-500",
  },
  {
   icon: Download,
   label: "Downloads",
   value: "—",
   color: "text-purple-500",
  },
  {
   icon: TrendingUp,
   label: "Engagement",
   value: "—",
   color: "text-orange-500",
  },
 ];

 return (
  <div className="min-h-screen flex flex-col">
   <main className="flex-1 bg-muted/30">
    {/* HEADER */}
    <section className="gradient-hero border-b py-12">
     <div className="container">
      <h1 className="text-4xl font-bold mb-2">
       Welcome back,{" "}
       <span className="bg-gradient-to-r from-primary to-accent bg-clip-text text-transparent">
        Student
       </span>
      </h1>
      <p className="text-muted-foreground">
       Manage your projects, track performance, and showcase your work.
      </p>

      <Button
       className="mt-4"
       variant="hero"
       onClick={() => navigate("/upload-project")}>
       <Plus className="h-4 w-4 mr-2" /> Upload New Project
      </Button>
     </div>
    </section>

    {/* STATS */}
    <section className="py-8">
     <div className="container grid grid-cols-2 md:grid-cols-4 gap-4">
      {stats.map((stat, index) => (
       <Card key={index} className="gradient-card shadow-soft">
        <CardContent className="pt-6 flex items-center gap-4">
         <div className={`p-3 rounded-lg bg-muted ${stat.color}`}>
          <stat.icon className="h-6 w-6" />
         </div>
         <div>
          <p className="text-2xl font-bold">{stat.value}</p>
          <p className="text-xs text-muted-foreground">{stat.label}</p>
         </div>
        </CardContent>
       </Card>
      ))}
     </div>
    </section>

    {/* PROJECT TABLE */}
    <section className="py-8">
     <div className="container">
      <Card className="gradient-card shadow-soft">
       <CardHeader>
        <CardTitle>Your Projects</CardTitle>
       </CardHeader>

       <CardContent>
        {loading ? (
         <p className="text-center py-6 text-muted-foreground">
          Loading your projects...
         </p>
        ) : myProjects.length === 0 ? (
         <p className="text-center py-6 text-muted-foreground">
          You haven't uploaded any projects yet.
         </p>
        ) : (
         <Table>
          <TableHeader>
           <TableRow>
            <TableHead>Title</TableHead>
            <TableHead>Status</TableHead>
            <TableHead className="text-right">Actions</TableHead>
           </TableRow>
          </TableHeader>

          <TableBody>
           {myProjects.map((project) => (
            <TableRow key={project._id}>
             <TableCell className="font-medium">
              <Link
               to={`/project/${project._id}`}
               className="hover:text-primary">
               {project.title}
              </Link>
             </TableCell>

             <TableCell>
              <Badge variant="secondary">{String(project.approved)}</Badge>
             </TableCell>

             <TableCell className="text-right">
              <div className="flex justify-end gap-2">
               <Button
                size="icon"
                variant="ghost"
                onClick={() => openEditPopup(project)}>
                <Edit className="h-4 w-4" />
               </Button>

               <Button
                size="icon"
                variant="ghost"
                onClick={() => deleteProject(project._id)}>
                <Trash2 className="h-4 w-4 text-destructive" />
               </Button>
              </div>
             </TableCell>
            </TableRow>
           ))}
          </TableBody>
         </Table>
        )}
       </CardContent>
      </Card>
     </div>
    </section>
   </main>

   <Footer />

   {/* EDIT POPUP */}
   <EditProjectPopup
    open={editOpen}
    onClose={() => setEditOpen(false)}
    project={selectedProject}
    onSave={handleEditSave}
   />
  </div>
 );
};

export default Dashboard;
